function hamburg(){const e=document.querySelector(".hamburger"),t=document.querySelector(".nav-menu");e.addEventListener("click",()=>{e.classList.toggle("active"),t.classList.toggle("active")}),document.querySelectorAll(".nav-link").forEach(n=>n.addEventListener("click",()=>{e.classList.remove("active"),t.classList.remove("active")}))}function openNav(){document.getElementById("mySidenav").style.width="250px",document.getElementById("main").style.marginLeft="250px",document.body.style.backgroundColor="rgba(0,0,0,0.4)"}function closeNav(){document.getElementById("mySidenav").style.width="0",document.getElementById("main").style.marginLeft="0",document.body.style.backgroundColor="white"}function menu(){const e=document.querySelector(".hamburger-menu"),t=document.querySelector(".container");e.addEventListener("click",()=>{t.classList.toggle("active")})}